import "./style.css";

function App() {
	// the content in the () is called JSX
	// no "class" in JS, only "className"
	// you can only return one element, in this case is <>
	return (
		<>
			{/* header */}
			<header className="header">
				<div className="logo">
					<img src="logo.png" alt="Today I Learned" />
					<h1>Hello world!</h1>
				</div>
				<button className="btn btn-large btn-share">
					Share a fact
				</button>
			</header>

			{/* fact form */}
			<NewFactForm />
			<main className="main">
				{/* category filter */}
				<CategoryFilter />

				{/* facts list */}
				<FactsList />
			</main>
		</>
	);
}

function NewFactForm() {
	return <form className="fact-form">Fact form</form>;
}

function CategoryFilter() {
	return <aside>Category filter</aside>;
}

function FactsList() {
	return <section>Facts list</section>;
}

export default App;
